mapper.update:
UPDATE board

	board_title = #{boardTitle},
	board_content = #{boardContent},
	board_user = #{boardUser}

WHERE board_no = #{boardNo}

mapper.select:
SELECT board_no boardNo , board_title boardTitle , board_content boardContent , board_user boardUser , updatedate , createdate FROM board
board_title LIKE CONCAT('%', #{searchWord}, '%')
ORDER BY board_no LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:
INSERT INTO board (
	board_title, board_content, board_user
) VALUES (
	#{boardTitle}, #{boardContent}, #{boardUser}
)
